import React, {useEffect, useState } from "react";
import "./contact.css";
import Navbar from "./Navbar";
import axios from 'axios';
{
  /*import pic1 from '../img/pic1.jpg'*/
}
const Contact = () => {
  const [name, setName] = useState('');
  const [contact, setContact] = useState('');
  const [comment, setComment] = useState('');
  const postdata = (props)=>{
    console.log(props)
    const url = 'http://localhost:8000/';
    const user = {name, contact, comment};
    axios.post(url, user).then((response)=>console.log(response));
    
  }



  return (
    <div classname="navbar">
      <Navbar></Navbar>
      <div>
        <div className="contactset">
          <div>
            <img
              className="sideimg"
              src="https://images.unsplash.com/photo-1636624477441-87988d54e932?crop=entropy&cs=tinysrgb&fit=max&fm=jpg&ixid=MnwxfDB8MXxyYW5kb218MHx8fHx8fHx8MTYzODA5MTY1Nw&ixlib=rb-1.2.1&q=80&w=1080"
              alt=""
            />
          </div>

          {/*<img className = "contactimg" src={pic1} width='300' height='300'/>*/}
          {/*<button type="button" class="btn" onclick = {()=>{}}>âš™</button>*/}
          <form onSubmit={function(event){
              event.preventDefault();
              console.log(event.target.contactName.value);
              console.log(event.target.contactcontact.value);
              console.log(event.target.contactcomment.value);
              
              setName(event.target.contactName.value);
              setContact(event.target.contactcontact.value);
              setComment(event.target.contactcomment.value);
              postdata(event.target.contactName.value);
            }}>
            <div className = "contactinput">
              <div className = "c_name">
                  <label htmlFor="contactName">
                    
                    <span className = "letter">Name</span> <span className="required">*</span>
                  </label>
                  <br/>
                  <textarea
                    type="text"
                    defaultValue=""
                    cols="40"
                    rows="2"
                    id="contactName"
                    name="contactName"
                    
                    
                  />
              </div>

              
              
              <div className = "c_contact" >
                  <label htmlFor="c_contacthtml">
                  <span className = "letter">Contact</span> <span className="required">*</span>
                  </label>
                  <br/>
                  <textarea
                    type="text"
                    defaultValue=""
                    cols="40"
                    rows="2"
                    id="contactcontact"
                    name="contactcontact"
                  
                  />
              </div>
              
              <div className = "c_comment" >
                  <label htmlFor="contactcommnet">
                  <span className = "letter">Comment </span><span className="required">*</span>
                  </label>
                  <br/>
                  <textarea
                    
                    defaultValue=""
                    rows="10"
                    cols="40"
                    id="contactcomment"
                    name="contactcomment"
                  
                  />
              </div>
              <input className = "subbutton" type = "submit"  value="Submit"/>
            </div>

          </form>
        </div>
      </div>
    </div>
  );
};

export default Contact;
