import "./Adminabout.css";
import AdminNavbar from "./AdminNavbar";
import React, {useEffect, useState } from "react";
import axios from 'axios';
export default function About() {
  const [fname, setName] = useState('');
  const [fsns2, setsns2] = useState('');
  const [faddress, setAddress] = useState('');
  const [fsns1, setsns1] = useState('');
  const [fphone_num, setPhone_num] = useState('');
  const [femail, setEmail] = useState('');
  const [fhistory, setHistory] = useState('');
  const [fsns3, setsns3] = useState('');
  const [abouts, setabouts] = useState([
    {adminName : fname,adminProfileImageUrl:'null',adminTitle:'null',adminIntro:'' }
  ])
  const patchdata = (props)=>{
    console.log(props)
    const url = 'api.1971artist.com:3000/admins/:adminIdx';
    const user = {fname,faddress,fphone_num,femail,fhistory };
    axios.patch(url, user).then((response)=>console.log(response));
    
  }
  return (
    <div className="adminnavbar">
      <AdminNavbar></AdminNavbar>
      <div>
        <section id="about">
          <div className="adminaboutall">
            
            <div className="adminaboutpic">
            
              <img
                
                src="https://images.unsplash.com/photo-1637541615205-8654ee01a032?crop=entropy&cs=tinysrgb&fit=max&fm=jpg&ixid=MnwxfDB8MXxyYW5kb218MHx8fHx8fHx8MTYzODE5NDYyNA&ixlib=rb-1.2.1&q=80&w=1080"
                width = "500"
                height = "500"
              
              />
              
            </div>
            <form onSubmit = {function(event){
              event.preventDefault();
              console.log(event.target.aboutme.value);
              console.log(event.target.fname.value);
              console.log(event.target.faddress.value);
              console.log(event.target.fdetail_address.value);
              console.log(event.target.fphone_num.value);
              console.log(event.target.femail.value);
              console.log(event.target.fhistory.value);
              
              setName(event.target.fname.value);
              setAddress(event.target.faddress.value);
              setDetail_address(event.target.fdetail_address.value);
              setPhone_num(event.target.fphone_num.value);
              setEmail(event.target.femail.value);
              setHistory(event.target.fhistory.value);
              patchdata(event.target.fname.value);
            }}>
              
              <div className="adminim">
  <input className = "savebtn" type = "submit"  value="save"/>
<h2><input type="text" placeholder = "Name" id="fname" name="fname" defaultvalue="name"/></h2> 
<h2><input type="text" placeholder = "Title" id="ftitle" name="ftitle" defaultvalue="title"/></h2>

<p>
<textarea
      className = "adminabouttext"
      placeHolder = "textarea"
      defaultValue=""
      rows="5"
      cols="55"
      id="aboutme"
      name="aboutme"
    
    />
</p>

<p>
<textarea
      className = "adminabouttext"
      placeHolder = "history"
      defaultValue=""
      rows="5"
      cols="55"
      id="fhistory"
      name="fhistory"
    
    />
</p>

<div className="row">
  <div className="columns contact-details">
    <p className="address">
      <br />
      <span>
      <input type="text" placeholder = "Address" id="faddress" name="faddress" defaultvalue="address"/>
        <br />
        <br />
      </span>

      <span><input type="text" placeholder = "Phone_num" id="fphone_num" name="fphone_num" defaultvalue="phone_num"/></span>
      <br />
      <br />
      <span><input type="text" placeholder = "Email" id="femail" name="femail" defaultvalue="email"/></span>
      <br />
      <br />
      <span><input type="text" placeholder = "sns1" id="fsns1" name="fsns1" defaultvalue="sns1"/></span>
      <br />
      <span><input type="text" placeholder = "sns2" id="fsns2" name="fsns2" defaultvalue="sns2"/></span>
      <br />
      <span><input type="text" placeholder = "sns3" id="fsns1" name="fsns3" defaultvalue="sns3"/></span>
    </p>
  </div>
</div>
</div>
          </form>
          </div>
        </section>
      </div>
    </div>
  );
}
