import React from "react";
import "./Picupload.css";
import AdminNavbar from "./AdminNavbar";
import { useState } from "react";
import axios from "axios";

const Picupload = () => {
  const [userFiles, setFiles] = useState("");
  const [userTitle, setTitle] = useState("");
  const [userIntro, setIntro] = useState("");
  const [userCate, setCate] = useState("");
  const [userIngr, setIngr] = useState("");

  const handleInputTitle = (e) => {
    setTitle(e.target.value);
  };
  const handleInputIntro = (e) => {
    setIntro(e.target.value);
  };

  const handleInputCate = (e) => {
    setCate(e.target.value);
  };
  const handleInputIngr = (e) => {
    setIngr(e.target.value);
  };

  const onLoadFile = (e) => {
    setFiles(URL.createObjectURL(e.target.files[0]));
  };

  const handleFileUpload = () => {
    const formData = new FormData();

    formData.append("userFile", userFiles);

    const data = {
      image: userFiles,
      postTitle: userTitle,
      postIntro: userIntro,
      postCategory: userCate,
      postIngredient: userIngr,
    };

    const config = {
      headers: {
        "x-access-token":
          "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJhZG1pbklkIjoxLCJpYXQiOjE2NDA2MTc5MDYsImV4cCI6MTY3MjE1MzkwNiwic3ViIjoidXNlckluZm8ifQ.aOC6HO4TRf__izxleFMD0EL-r0t9d-X22GY3Zx_Disk",
      },
    };

    axios
      .post("http://api.1971artist.com:3000/posts/1", data)
      .then((res) => {
        console.log(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };
  return (
    <div className="adminnavbar">
      <AdminNavbar></AdminNavbar>
      <div className="down">
        <div className="f_container">
          <div className="img">
            <img src={userFiles} alt="" width="400" height="400" />
            <input
              type="file"
              id="image"
              accept="img/*"
              onChange={onLoadFile}
            />
          </div>

          <div className="setting">
            <br />
            <textarea
              type="text"
              placeholder="제목"
              cols="50"
              rows="3"
              id="uploadcomment"
              value={userTitle}
              onChange={handleInputTitle}
            />
            <br />
            <br />

            <textarea
              type="text"
              placeholder="설명"
              cols="50"
              rows="6"
              id="uploadcomment"
              value={userIntro}
              onChange={handleInputIntro}
            />
            <br />
            <br />
            <textarea
              type="text"
              placeholder="재료"
              cols="50"
              rows="3"
              id="uploadcomment"
              value={userIngr}
              onChange={handleInputIngr}
            />
            <br />
            <br />
            <div className="wrapper">
              <div className="p">
                <input
                  type="checkbox"
                  name="painting"
                  value={userCate}
                  onChange={handleInputCate}
                />
                painting
              </div>
              <div className="s">
                <input
                  type="checkbox"
                  name="sculpture"
                  value={userCate}
                  onChange={handleInputCate}
                />
                sculpture
              </div>
              <button className="upbtn" onClick={handleFileUpload}>
                업로드
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Picupload;
