import React from "react";
import "./Picupload.css";
import AdminGallerynav from "./Admingallerynav";
import AdminNavbar from "./AdminNavbar";
import { Component, useState } from "react";
import axios from "axios";


const Picupload = () => {
    const [uploadcomment, setuploadcomment] = useState("");
    const [uploadtitle, setuploadtitle] = useState("");
    const [uploadingr, setuploadingr] = useState("");
    const [uploadcate, setuploadcate] = useState(""); 
    const postdata = (props) => {
        console.log(props);
        
    axios.post('http://api.1971artist.com:3000/contacts/1',JSON.stringify({
    //   imgage: name,
      postTitle: uploadtitle,
      postIntro: uploadcomment,
      postCategory: uploadcate,
      postIngredient:uploadingr
    }),
    {
      headers: 
        {"Content-Type": 'application/json',
         "x-access-token" : 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJhZG1pbklkIjoxLCJpYXQiOjE2NDA2MTc5MDYsImV4cCI6MTY3MjE1MzkwNiwic3ViIjoidXNlckluZm8ifQ.aOC6HO4TRf__izxleFMD0EL-r0t9d-X22GY3Zx_Disk'
        }
    }
    ).then((response) => console.log(response));
};
  const [files, setFiles] = useState("");
  
  const onLoadFile = (e) => {
    setFiles(URL.createObjectURL(e.target.files[0]));};
    return (
        <div className="adminnavbar">
        <AdminNavbar></AdminNavbar>
        <div className ="down">
            
            <div className = "f_container">
                
                <div className = "img">
                <img src={files} width="400" height="400" />
                <input
                type="file"
                id="image"
                accept="img/*"
                onChange={onLoadFile}
                />
                
                </div>
            <form
            onSubmit={function (event) {
              event.preventDefault();


            //   setuploadcate(event.target.uploadcate.value);
              setuploadcomment(event.target.uploadcomment);
              setuploadtitle(event.target.uploadtitle.value);
              setuploadingr(event.target.uploadingr.value);
              postdata(event.target.contactName.value);
            }}
          >
                <div className = "setting">
                <br/>
                    <textarea
                        type = "text"
                        placeholder= "제목"
                        cols = "50"
                        rows = "3"
                        id = "uploadtitle"
                        name = "uploadtitle"
                    />
                    <br/><br/>

                    <textarea
                        type = "text"
                        placeholder= "설명"
                        cols = "50"
                        rows = "6"
                        id = "uploadintro"
                        name = "uploadintro"
                    />
                    <br/><br/>
                    <textarea
                        type = "text"
                        placeholder= "재료"
                        cols = "50"
                        rows = "3"
                        id = "uploadingr"
                        name = "uploadingr"
                    />
                    <br/><br/>
                    <div className = "wrapper">
                    <div className = "p"><input type="checkbox" name = "painting" value = "painting"/>painting</div>
                    <div className = "s"><input type="checkbox" name = "sculpture" value = "sculpture"/>sculpture</div>
                    <button className="upbtn" type="submit"> 업로드</button>
                    </div>
                </div>
            </form>
            </div>
        </div>
    </div>
    );
  };
  
  export default Picupload;
