import React, { useEffect, useState } from "react";
import "./header.css";
import Navbar from "./Navbar";
import pic1 from "../img/pic1.jpg";
import pic2 from "../img/pic2.jpg";
import axios from "axios";
import Headerimg from "./Headerimg";
export default function Header() {
  const [container, setcontainer] = useState("container");
  const [img1, setimg1]= useState();
  const [img2, setimg2]= useState();
  const [img3, setimg3]= useState();
  const [imgall, setimg]=useState();
  useEffect(()=>{axios.get('http://api.1971artist.com:3000/admins/backgrounds/1')
  .then(response => {
    console.log(response.data);
    setimg(response.data);
    // imgall&&imgall.result.map((result)={
      
    // })
    
     console.log(img1);
  })
  },[])
  const imgList = imgall&&imgall?.result.map((result)=>(<div>{result.backgroundImageUrl}</div>));
  let oneimg = imgList.pop();
    // setimg1(imgList);
  const changeimg1 = () => {
    console.log("1");
    setcontainer("container");
  };
  const changeimg2 = () => {
    console.log("2");
    setcontainer("container2");
  };
  const changeimg3 = () => {
    console.log("3");
    setcontainer("container3");
  };
  const changeleft = () =>{
    console.log("le");
    
    if(container==="container2")
    {
      setcontainer("container"); 
    }
    if(container=="container3")
    {
      setcontainer("container2"); 
    }
  }
  const changeright = () =>{
    console.log("ri");
    if(container==="container")
    {
      setcontainer("container2"); 
    }
    if(container=="container2")
    {
      setcontainer("container3"); 
    }
  }
  
  return (
    <div classname="navbar">
      <Navbar></Navbar>
      <div className="header">
        <div className="headerTitles">
          <span className="headerTitle">Choi WooSun</span>
        </div>
        <div className="over">
          <div className={container}>
            <div className="inner">
              <img
                className="headerImg"
                src="https://gearbox.imgix.net/https%3A%2F%2Fix-www.imgix.net%2Fsolutions%2Fbeach.jpg?w=1782&h=982&fit=crop&ixlib=js-2.0.0&s=696f7285735b866832b0d79abb471548"
                alt=""
              ></img>
            </div>
            <div className="inner">
              <img className="headerImg" src={img1} alt=""></img>
            </div>
            <div className="inner">
              <img className="headerImg" src={pic2} alt=""></img>
            </div>
          </div>
        </div>
        <div className="btns">
          <button className= "left" onClick={changeleft}>[</button>
          
          <button className="btn1" onClick={changeimg1}>
            1
          </button>
          <button className="btn2" onClick={changeimg2}>
            2
          </button>
          <button className="btn3" onClick={changeimg3}>
            3
          </button>
          <button className= "right" onClick={changeright}>]</button>
        </div>
        <div>
          {/* <Headerimg imgall={imgall}/> */}
         {oneimg}
        </div>
      </div>
    </div>
  );
}
